module.exports.$hideName = function (name) {
    var length = name.length;
    var temp = "";
    for (var i = 0; i < length - 2; i++) {
        temp += "*";
    }
    if (length <= 2) {
        name = name.substring(0, 1) + "*";
    } else {
        name = name.substring(0, 1) + temp + name.substring(length - 1, length);
    }
    return name;
}